diagram_graphviz:
digraph Architecture {
  node [shape=circle, style=filled, fillcolor=skyblue];

  Frontend [label="Frontend (React.js)", fillcolor=lightgreen];
  Backend [label="Backend (Java Spring Boot)", fillcolor=orange];
  Database [label="Database (MongoDB)", fillcolor=yellow];
  Cloud [label="Cloud (AWS)", fillcolor=lightcoral];
  DataIngestion [label="Data Ingestion", fillcolor=lightblue];
  SentimentAnalysis [label="Sentiment Analysis", fillcolor=lightblue];
  API [label="API", fillcolor=lightblue];
  Dashboard [label="Dashboard", fillcolor=lightblue];

  Frontend -> API [label="Requests Data"];
  API -> Backend [label="Processes Request"];
  Backend -> SentimentAnalysis [label="Analyzes Sentiment"];
  Backend -> DataIngestion [label="Ingests Data"];
  Backend -> Database [label="Stores/Retrieves Data"];
  Cloud -> Database [label="Hosts"];
  Cloud -> Backend [label="Hosts"];
  Cloud -> Frontend [label="Hosts"];
  DataIngestion -> SentimentAnalysis [label="Sends Data"];
  SentimentAnalysis -> Database [label="Stores Results"];
  Dashboard -> Frontend [label="Displays"];

  subgraph cluster_modules {
    node [style=filled, fillcolor=white];
    label = "Modules";
    DataIngestion; SentimentAnalysis; API; Dashboard;
  }

  subgraph cluster_tech {
    node [style=filled, fillcolor=white];
    label = "Technology Stack";
    Frontend; Backend; Database; Cloud;
  }
}
